css:
@import url('https://fonts.googleapis.com/css2?family=Dosis:wght@300&display=swap');

* {
    font-family: 'Dosis', sans-serif!important;
    font-size : 13px;
}

.red-color {
    /*color : white;*/
    /*background-color : #d80000;*/
    /*border: 1px solid ;*/
    font-weight : bold;
}


.header {
    display :flex;
    margin-bottom:25px;
}
.company-details {
    flex-basis : 40%;
}

.header-breack {
    flex-basis : 20%;
}

.invoice-header {
    flex-basis : 40%;
    display:flex;
    text-align : center;
}
.header-invoice-column {
    flex-basis :100%;
}

.header-invoice-row {
    flex-basis:100%;
    border : 1px solid;
}
.details {
    display : flex;
    margin-bottom:5px;
    text-align:center;
}
.breack {
    flex-basis : 5%;
}
.notify-details  ,  .consignee-details {
    display : flex;
    flex-basis : 47.5%;
}

.notify-details-column  , .consignee-details-column {
    flex-basis : 25%;
}
.notify-details-column-details , .consignee-details-column-details {
    /*border : 1px solid;*/
    flex-basis :75%;

}

.consignee-details-row , .notify-details-row {
    border :1px solid;
}


.consignee-details-row-details , .notify-details-row-details {
    flex-basis : 60%;
}
.table-items { 
    text-align : center!important;
    margin : 20px 0px;
}

.print-format th , .print-format td  {
    padding : 2px !important;
}
.print-format thead {
    font-size :12px!important;
}

.section-terms-and-calculations {
    display : flex;
    flex-direction: row ;

    gap: 40px 0px;

}

.child-one {
    margin-top:10px;
    flex-basis : 55%;
    border : 1px solid;
    margin-right: 100px ;
    /*padding:5px;*/
}
.padding {
    padding: 0px 0px 0px 3px;
}
.child-two {
    margin-top:2px;
    display:flex;
    /*width:100%;*/
    flex-basis : 30%;
}

.table-column {
    margin-bottom: 1em;
    width: 100%;
}

.table-row {
    border: 1px solid black;
    height: 1.5em;
