css:
.title {
    text-align: center;
    font-weight: bold;
    font-size: 25px;
}

.section-one-header {
    display: flex;
}

.section-one-header-parent {
    flex-basis: 25%;
    font-weight: normal !important;
}

/* section item */
.item th {
    background-color: white !important;
    border-bottom: 0px;
    color: black;
    padding: 2px !important;
    vertical-align: middle !important;
    font-size: 14;
}

.item td {
    text-align: center;
    padding: 2px !important;
    font-size: 14;
}

.total td {
    text-align: left !important;
    padding: 2px !important;
    font-size: 14;
}

/* customer-details */
.customer-details h4 {
    /*margin:0!important;*/
    font-size: 14px !important;
}

.section-three {
    margin-top: 10px;
    display: flex;
    gap: 10%;
}

.signature,
.shipment-details {
    flex-basis: 45%;
}

.signature-box {
    height: 150px;
    width: 300px;
    border: 1px solid;
}

.shipment-details-row {
    display: flex;
    /*border:1px solid;*/
}

.row-answer {
    flex-basis: 30%;
    text-align: left;
    padding: 0 5px;
}

.row-title {
    flex-basis: 70%;
    text-align: left;
    padding: 0 5px;
}

.footer,
.footer p {
    /*display:flex;*/
    text-align: center;
    margin: 0px 0px 0px !important;
}

